i, pos = string.unpack(">i9", "\0\0\0\0\0\0\0\0\1")
assert(pos == 10  and i == 1)
i, pos = string.unpack("<i9", "\1\0\0\0\0\0\0\0\0")
assert(pos == 10  and i == 1)
i, pos = string.unpack(">i10", "\255\255\255\255\255\255\255\255\255\253")
assert(pos == 11  and i == -3)
i, pos = string.unpack("b", "\128")
assert(pos == 2 and i == -128)
i, pos = string.unpack("b", "\130")
assert(pos == 2 and i == -126)
